import React, { useRef } from 'react';
import PropTypes from 'prop-types';
import { Button, Clickable, FormikInput } from 'phoenix-components';
import primaryPlusIcon from 'assets/images/testimonials/primaryplus.svg';
import { ImagePicker } from 'components/common/ImagePicker';
import defaultPicIcon from 'assets/images/testimonials/defaultpic.svg';
import { DeleteAlert } from 'components/shared/DeleteAlert';
import Alert from 'components/shared/alert/Alert';
import {
  getVideosAndImages,
} from 'components/products/variantUtils';
import { Drawer } from 'components/shared/Drawer';
import Loader from 'services/loader';
import SnackBar from 'services/snackbar';
import { useTestimonials } from 'hooks/common';
import editIcon from 'assets/v2/settings/domain/edit.svg';
import closeIcon from 'assets/images/testimonials/close.svg';
import { Formik } from 'formik';
import { FooterButton } from 'components/common/FooterButton';
import { useToggle } from 'hooks/common';
import { SideDrawer } from 'components/shared/SideDrawer';
import { useDesktop } from 'contexts';
import { Becca } from '../../api';
import getInitialValues from './TestimonialsUtilis';
import { tSchema } from './TestimonialsUtilis';
import styles from './AddTestimonials.module.css';

export default function AddTestimonials({
  edit, onClose, updateTestimonial, handleRef
}) {
  const initialValues = edit ? updateTestimonial : getInitialValues();
  const [pp, setPp] = useToggle(false);
  const [imgOrTxt, toggleImgOrTxt] = useToggle(false);
  const [updateTestimonalPhoto, toggleUpdateTestimonalPhoto] = useToggle(false);
  const [openAlert, toggleAlert] = useToggle();
  const [data] = useTestimonials();
  const inputRef1 = useRef(0);
  const isDesktop = useDesktop();
  const Component = isDesktop ? SideDrawer : Drawer;

  const onSave = async (values) => {
    if (values.testimonialImage !== '' && values.message !== '') {
      toggleImgOrTxt();
      return;
    }
    if (values.testimonialImage === '' && values.message === '') {
      toggleAlert();
      return;
    }
    const msg = edit ? 'Testimonial updated successfully' : 'Testimonial added successfully';
    if (edit) {
      if (JSON.stringify(values) === JSON.stringify(updateTestimonial)) {
        onClose();
        return;
      }
      data.forEach((item, index) => {
        if (item._id === updateTestimonial._id) {
          data.splice(index, 1);
        }
      });
    }
    try {
      Loader.show();
      await Becca.updateUiConfig('testimonials', [...data, values]);
      handleRef();
      SnackBar.show(msg, 'success');
      onClose();
    } catch (e) {
      SnackBar.showError(e);
    } finally {
      Loader.hide();
    }
  };

  const uploadProfilePic = async (e, setValue) => {
    try {
      Loader.show();
      const files = e?.target?.files;
      const file = files && files[0];
      const payload = new FormData();
      payload.append('name', file.name);
      payload.append('purpose', 'Testimonial');
      payload.append('type', 'image');
      payload.append('file', file);
      const { url } = await Becca.uploadAsset(payload);
      setValue('customerImage', url);
      setPp();
      SnackBar.show('upload success');
    } catch (e) {
      SnackBar.show('upload failed', 'error');
    } finally {
      Loader.hide();
    }
  };

  const onImageChange = (values, setField) => (files) => {
    if (files.length === 0) {
      setField('testimonialImage', '');
      return;
    }
    const { images } = getVideosAndImages(files);
    setField('testimonialImage', images[0]?.url);
    setField('type', 'image');
  };

  const handleDeleteTxt = (setValue) => {
    setValue('message', '');
    toggleImgOrTxt();
  };

  return (
    <Component
      title={edit ? 'Edit Testimonial' : 'Add Testimonial'}
      backButton
      onClose={onClose}
    >
      <Formik
        initialValues={initialValues}
        validationSchema={tSchema}
        onSubmit={onSave}
      >
        {({
          values, setFieldValue, submitForm
        }) => (
          <div className={styles.mainContainer}>
            <FormikInput
              type="textarea"
              rows={5}
              label="Customer testimonials"
              name="message"
              placeholder="Add a testimonial from your customer"
            />
            <div className={styles.max}>
              Maximum 150 charaters
              {edit && (<span>{`(remaining ${150 - values.message.length})`}</span>)}
            </div>
            <div className={styles.orBorderMain}>
              <div className={styles.orBorder}>
                <span className={styles.orTxt}>Or</span>
              </div>
              {values.message.length > 150 && (
                <Alert
                  btnText="Ok"
                  text="Maximun charaters allowed is 150"
                  onClick={setFieldValue('message','')}
                />
              )}
            </div>
            {!edit && (
              <ImagePicker
                onChange={onImageChange(values, setFieldValue)}
                isMulti={false}
                images={values?.testimonialImage ? [{ url: values.testimonialImage }] : []}
                accept="image/*"
                showCover={false}
                videos={false}
                label="Upload Image"
                showLimited={false}
              />
            )}
            {edit && (
              <>
                {(updateTestimonalPhoto || values.testimonialImage === '')
                  && (
                    <ImagePicker
                      onChange={onImageChange(values, setFieldValue)}
                      isMulti={false}
                      images={values?.testimonialImage ? [{ url: values.testimonialImage }] : []}
                      accept="image/*"
                      showCover={false}
                      videos={false}
                      label="Upload Image"
                    />
                  )}
                {(updateTestimonalPhoto === false && values.testimonialImage !== '') && (
                  <div className={styles.testimonialImageCon}>
                    <Clickable
                      className={styles.editIcon}
                      onClick={() => {
                        setFieldValue('testimonialImage', '');
                        setFieldValue('type', 'message');
                        toggleUpdateTestimonalPhoto();
                      }}
                    >
                      <img src={editIcon} alt="" />
                    </Clickable>
                    <img
                      src={values.testimonialImage}
                      alt=""
                      className={styles.uploadedTestimonialImg} />
                  </div>
                )}
              </>
            )}
            <div className={styles.csTitle}>
              Customer Details
            </div>
            <div className={styles.customerName}>
              <FormikInput
                label="Customer Name"
                name="name"
                placeholder="e.g.  John Deo"
              />
            </div>
            <div className={styles.ciMainContainer}>
              <div className={styles.section1}>
                <div className={styles.ciTitle}>
                  Select Customer Image
                  <span className={styles.optional}>(optional)</span>
                </div>
                <input
                  ref={inputRef1}
                  accept="image/*"
                  hidden
                  type="file"
                  name="profilePic"
                  onChange={(e) => { uploadProfilePic(e, setFieldValue); }}
                />
                {(pp === false && edit === false) && (
                  <Clickable
                    onClick={() => {
                      // eslint-disable-next-line no-unused-expressions
                      inputRef1?.current?.click();
                    }}
                  >
                    <div className={styles.add}>
                      <img src={primaryPlusIcon} alt="" />
                      <div className={styles.addTxt}>
                        Add
                      </div>
                    </div>
                  </Clickable>
                )}
                {edit && (
                  <Clickable
                    onClick={() => {
                      // eslint-disable-next-line no-unused-expressions
                      inputRef1?.current?.click();
                    }}
                  >
                    <div className={styles.add}>
                      <img src={primaryPlusIcon} alt="" />
                      <div className={styles.addTxt}>
                        Add
                      </div>
                    </div>
                  </Clickable>
                )}
              </div>
              <div className={styles.ciProfilePicCon}>
                {edit === false && (
                  <>
                    <img
                      src={values.customerImage === '' ? defaultPicIcon : values.customerImage}
                      alt=""
                      className={styles.ciProfilePic} />
                    {pp && (
                      <Clickable
                        className={styles.clsBtn}
                        onClick={() => {
                          setFieldValue('customerImage', '');
                          setPp();
                        }}>
                        <img src={closeIcon} alt="" />
                      </Clickable>
                    )}
                  </>
                )}
                {
                  edit && (
                    <>
                      <img
                        src={values.customerImage === '' ? defaultPicIcon : values.customerImage}
                        alt=""
                        className={styles.ciProfilePic} />
                      <Clickable
                        className={styles.clsBtn}
                        onClick={() => { setFieldValue('customerImage', ''); }}>
                        <img src={closeIcon} alt="" />
                      </Clickable>
                    </>
                  )
                }
              </div>
            </div>
            <div className={styles.designation}>
              <FormikInput
                label="Designation (optional)"
                name="designation"
                placeholder="Enter Designation"
              />
            </div>
            <div className={styles.location}>
              <FormikInput
                label="Loction (optional)"
                name="location"
                placeholder="Enter Location"
              />
            </div>
            {imgOrTxt
              && (
                <DeleteAlert
                  title="You can add either image or text. Should we delete the text?"
                  onCancel={toggleImgOrTxt}
                  primary="Yes"
                  secondary="Cancel"
                  position={false}
                  onDelete={() => {
                    handleDeleteTxt(setFieldValue);
                  }}
                />
              )}
            {openAlert && (
              <Alert
                btnText="Ok"
                text="Image or Text is required"
                onClick={toggleAlert}
              />
            )}
            {!isDesktop
              && (
                <FooterButton>
                  {!edit && (<Button label="Save" className={styles.saveBtn} onClick={submitForm} />)}
                  {edit && (
                    <Button
                      label="Save"
                      className={styles.saveBtn}
                      onClick={() => {
                        submitForm();
                      }} />
                  )}
                </FooterButton>
              )}
            {
              isDesktop && (
                <div className={styles.saveBtnDeskCon}>
                  {!edit && (<Button label="Save" className={styles.saveBtn} onClick={submitForm} />)}
                  {edit && (
                    <Button
                      label="Save"
                      className={styles.saveBtn}
                      onClick={() => {
                        submitForm();
                      }} />
                  )}
                </div>
              )
            }
          </div>
        )}
      </Formik>
    </Component>
  );
}

AddTestimonials.propTypes = {
  edit: PropTypes.bool.isRequired,
  onClose: PropTypes.func.isRequired,
  updateTestimonial: PropTypes.object,
  handleRef: PropTypes.func.isRequired
};

AddTestimonials.defaultProps = {
  updateTestimonial: {}
};
