import React from 'react'
import styled from 'styled-components'
import { Directions } from './FloatingMenu'

// @ts-ignore
const Wrapper = styled(
  ({ isOpen, slideSpeed, background, size, spacing, direction, ...rest }) => (
    <li {...rest} />
  )
)(
  // @ts-ignore
  ({ isOpen, slideSpeed, background, size, spacing, direction }: any) => ({
    background,
    display: 'flex',
    border: 'none',
    borderRadius: '50%',
    boxShadow: '0 0 4px rgba(0,0,0,.14),0 4px 8px rgba(0,0,0,.28)',
    cursor: 'pointer',
    outline: 'none',
    padding: '0',
    WebkitUserDrag: 'none',
    fontWeight: 'bold',
    justifyContent: 'center',
    alignItems: 'center',
    opacity: isOpen ? 1 : 0,
    transition: `all ${slideSpeed}ms`,
    width: size,
    height: size,
    marginTop: direction === 'down' ? spacing : 0,
    marginBottom: direction === 'up' ? spacing : 0,
    marginLeft: direction === 'right' ? spacing : 0,
    marginRight: direction === 'left' ? spacing : 0,
    pointerEvents: isOpen ? 'auto' : 'none'
  })
)

export interface ChildButtonProps {
  icon?: any
  direction?: Directions
  index?: number
  size?: number
  spacing?: number
  isOpen?: boolean
  onClick?: any
  background?: string
}

const ChildButton = ({
  direction = Directions.Up,
  index = 1,
  size = 40,
  spacing = 0,
  isOpen = false,
  onClick = null,
  icon,
  ...rest
}: ChildButtonProps) => {
  const offsetX =
    direction === 'right'
      ? (size + spacing) * index
      : direction === 'left'
      ? (size + spacing) * index * -1
      : 0
  const offsetY =
    direction === 'down'
      ? (size + spacing) * index
      : direction === 'up'
      ? (size + spacing) * index * -1
      : 0

  return (
    <Wrapper
      isOpen={isOpen}
      size={size}
      spacing={spacing}
      direction={direction}
      {...rest}
      onClick={isOpen ? onClick : null}
      style={{
        transform: `translate(${isOpen ? 0 : -offsetX}px, ${
          isOpen ? 0 : -offsetY
        }px)`
      }}
    >
      {icon}
    </Wrapper>
  )
}

export default ChildButton
