import React from 'react';
import { Drawer } from 'components/shared/Drawer';
import { useHistory } from 'react-router-dom';
import { Clickable } from 'phoenix-components';
import plusIcon from 'assets/images/testimonials/plus.svg';
import { FooterButton } from 'components/common/FooterButton';
import { useTestimonials } from 'hooks/common';
import { Loading } from 'components/shared/Loading';
import { useToggle } from 'hooks/common';
import { useState } from 'react';
import { SideDrawer } from 'components/shared/SideDrawer';
import { useDesktop } from 'contexts';
import DisplayTestimonials from './ DisplayTestimonials';
import styles from './DisplayTestimonials.module.css';
import AddTestimonials from './AddTestimonials';

function Testimonials() {
  const history = useHistory();
  const [addDetails, toggleAddDetails] = useToggle(false);
  const [editStatus, toggleEditStatus] = useToggle(false);
  const isDesktop = useDesktop();
  const Component = isDesktop ? SideDrawer : Drawer;
  const [updateTestimonial, setUpdateTestimonial] = useState('');
  const [data, loading, refresh] = useTestimonials();

  const handleEdit = (testimonialDetails) => {
    toggleAddDetails();
    toggleEditStatus();
    setUpdateTestimonial(testimonialDetails);
  };

  const handleOnClose = () => {
    toggleAddDetails();
    if (editStatus === true) {
      toggleEditStatus();
      setUpdateTestimonial('');
    }
  };

  const handleRef = () => {
    refresh();
  };

  if (loading) {
    return <Loading />;
  }

  return (
    <Component
      title="Customer testimonials"
      backButton
      onClose={() => history.goBack()}
    >
      {!addDetails && (
        <>
          <DisplayTestimonials
            list={data}
            handleEdit={handleEdit}
            handleRef={handleRef}
          />
          {!isDesktop
            && (
              <FooterButton>
                <Clickable
                  className={styles.addBtn}
                  onClick={toggleAddDetails}
                >
                  <img className={styles.addImg} src={plusIcon} alt="" />
                  <span className={styles.btnText}> Add New Testimonials </span>
                </Clickable>
              </FooterButton>
            )}
          {isDesktop && (
            <div className={styles.addBtnDeskCon}>
              <Clickable
                className={styles.addBtnDesk}
                onClick={toggleAddDetails}
              >
                <img className={styles.addImg} src={plusIcon} alt="" />
                <span className={styles.btnText}> Add New Testimonials </span>
              </Clickable>
            </div>
          )}

        </>
      )}
      {addDetails && (
        <>
          <AddTestimonials
            edit={editStatus}
            onClose={handleOnClose}
            updateTestimonial={updateTestimonial}
            handleRef={handleRef}
          />
        </>
      )}
    </Component>
  );
}

export default Testimonials;
