import React from 'react';
import PropTypes from 'prop-types';
import emptyBirdIcon from 'assets/images/testimonials/emptyBird.svg';
import attentionIcon from 'assets/images/testimonials/attention.svg';
import closeIcon from 'assets/images/testimonials/close.svg';
import cx from 'classnames';
import TestimonialsCard from './TestimonialsCard';
import styles from './DisplayTestimonials.module.css';
export default function DisplayTestimonials({ list, handleEdit, handleRef }) {
  const isEmptyTestimonials = list?.length === 0;
  const [closeProTip, setProTip] = useToggle(false);

  const getTestimonials = (testimonials, i) => (
    <TestimonialsCard
      testimonials={testimonials}
      count={i}
      handleEdit={handleEdit}
      handleRef={handleRef}
    />
  );
  return (
    <>
      <div
        styles={cx(styles.mainContainer)}>
        {isEmptyTestimonials ? (
          <div className={styles.emptyMainContainer}>
            <div className={styles.imageContainer}>
              <img src={emptyBirdIcon} alt="" />
              <div className={styles.emptyTxt}>You have not testimonials saved!</div>
            </div>
            {!closeProTip
            && (
              <div className={styles.proTipMainConatiner}>
                <div className={styles.proTipSubContainer}>
                  <div className={styles.closeBtnContainer} onClick={setProTip}>
                    <img src={closeIcon} alt="" />
                  </div>
                  <div className={styles.section1}>
                    <img src={attentionIcon} alt="" className={styles.attentionIcon} />
                    <div className={styles.proTipTxt}>
                      Pro tip
                    </div>
                  </div>
                  <div className={styles.proTipDescription}>
                    Do you know? 72% of consumers say positive testimonials
                    and reviews increase their trust in a business
                  </div>
                </div>
              </div>
            ) }
          </div>
        ) : (
          <div className={styles.testimonialsMainContainer}>
            {
              list?.map((testimonials, i) => (
                getTestimonials(testimonials, i)
              ))
            }
          </div>
        )}
      </div>
    </>
  );
}

DisplayTestimonials.propTypes = {
  list: PropTypes.object.isRequired,
  handleEdit: PropTypes.func.isRequired,
  handleRef: PropTypes.func.isRequired
};

DisplayTestimonials.defaultProps = {
};
